// src/components/Summary.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./../Form.css"; // Import the shared styles

const Summary = () => {
  const { formData } = useContext(FormDataContext);
  const navigate = useNavigate();

  const handleBack = () => {
    navigate("/step-four");
  };

  const handleFinish = () => {
    alert("Thank you! Your information has been reviewed.");
    navigate("/");
  };

  return (
    <div className="form-container">
      {/* Progress bar container with dynamic width */}
      <div className="progress-bar-container">
        <div className="progress-bar" style={{ width: "100%" }}></div>
      </div>
      <h1>Review Your Information</h1>
      <div className="summary-details">
        <p>
          <strong>Currently Insured:</strong> {formData.insured}
        </p>
        <p>
          <strong>Age Group:</strong> {formData.ageGroup}
        </p>
        <p>
          <strong>Number of Drivers:</strong> {formData.numDrivers}
        </p>
        <p>
          <strong>Number of Cars:</strong> {formData.carCount}
        </p>
        <p>
          <strong>Interested in More Info:</strong>{" "}
          {formData.moreInfo ? "Yes" : "No"}
        </p>
        <p>
          <strong>Owns Home:</strong> {formData.homeOwnership ? "Yes" : "No"}
        </p>
        <p>
          <strong>Interested in Bundling Insurance:</strong>{" "}
          {formData.bundleInterest ? "Yes" : "No"}
        </p>
      </div>

      <div className="buttons-container">
        <button onClick={handleFinish}>Finish</button>
        <button className="secondary-button" onClick={handleBack}>
          Back to Edit
        </button>
      </div>
    </div>
  );
};

export default Summary;
