import React, { useState } from "react";
import "../../styles/flowTwo/StepOne2.css";
import Header from "../UserFlowOne/Header";
import Footer from "../UserFlowTwo/Footer";
import { vehicleMakes } from "../../constants/vehicleMakes";
import { vehicleModel } from "../../constants/vehicleModel";
import { vehicleType } from "../../constants/vehicleType";

const StepOne2 = ({ nextStep }) => {
  const [vehicles, setVehicles] = useState([
    {
      year: "",
      make: "",
      model: "",
      type: "",
      ownership: "Owned (not making loan payments)",
      mileage: "10,000 miles",
      primaryUse: "Commute to Work",
    },
  ]);
  const [errors, setErrors] = useState([]);

  const handleChange = (index, e) => {
    const { name, value } = e.target;
    const updatedVehicles = [...vehicles];
    updatedVehicles[index][name] = value;

    // Reset dependent fields when higher-level fields change
    if (name === "make") {
      updatedVehicles[index].model = "";
      updatedVehicles[index].type = "";
    } else if (name === "model") {
      updatedVehicles[index].type = "";
    }

    setVehicles(updatedVehicles);

    // Clear error for the changed field
    const updatedErrors = [...errors];
    updatedErrors[index] = {
      ...updatedErrors[index],
      [name]: "",
    };
    setErrors(updatedErrors);
  };

  const validateFields = (index) => {
    const vehicle = vehicles[index];
    const newErrors = {};
    if (!vehicle.year) newErrors.year = "Vehicle year is required.";
    if (!vehicle.make) newErrors.make = "Vehicle make is required.";
    if (!vehicle.model) newErrors.model = "Vehicle model is required.";
    if (!vehicle.mileage) newErrors.mileage = "Mileage is required.";
    if (!vehicle.primaryUse) newErrors.primaryUse = "Primary use is required.";

    const updatedErrors = [...errors];
    updatedErrors[index] = newErrors;
    setErrors(updatedErrors);

    return Object.keys(newErrors).length === 0;
  };

  const handleAddVehicle = () => {
    setVehicles([
      ...vehicles,
      {
        year: "",
        make: "",
        model: "",
        type: "",
        ownership: "Owned (not making loan payments)",
        mileage: "10,000 miles",
        primaryUse: "Commute to Work",
      },
    ]);
    setErrors([...errors, {}]);
  };

  const handleRemoveVehicle = (index) => {
    const updatedVehicles = vehicles.filter((_, i) => i !== index);
    const updatedErrors = errors.filter((_, i) => i !== index);
    setVehicles(updatedVehicles);
    setErrors(updatedErrors);
  };

  const handleSubmit = () => {
    const isValid = vehicles.every((_, index) => validateFields(index));
    if (isValid) {
      nextStep(vehicles);
    }
  };

  return (
    <div>
      <Header />
      <div className="vehicle-details-container">
        <h2>Get the Best Rates in Your Area!</h2>
        <p>
          Get your quote in just 3 simple steps. Enter your information below.
        </p>

        {vehicles.map((vehicle, index) => (
          <div key={index} className="vehicle-form-section">
            <div className="section-header">
              <span className="section-number">{index + 1}</span>
              <h3>About Your Vehicle {index + 1}</h3>
            </div>

            <div className="form-section">
              <label htmlFor={`year-${index}`}>
                Vehicle Year
                {errors[index]?.year && (
                  <span className="error-message">{errors[index].year}</span>
                )}
              </label>
              <select
                id={`year-${index}`}
                name="year"
                value={vehicle.year}
                onChange={(e) => handleChange(index, e)}
                className={errors[index]?.year ? "error-field" : ""}
              >
                <option value="">Please Select</option>
                {Array.from(
                  new Array(71),
                  (_, i) => new Date().getFullYear() - i
                ).map((year) => (
                  <option key={year} value={year}>
                    {year}
                  </option>
                ))}
              </select>

              <label htmlFor={`make-${index}`}>
                Vehicle Make
                {errors[index]?.make && (
                  <span className="error-message">{errors[index].make}</span>
                )}
              </label>
              <select
                id={`make-${index}`}
                name="make"
                value={vehicle.make}
                onChange={(e) => handleChange(index, e)}
                className={errors[index]?.make ? "error-field" : ""}
              >
                <option value="">Please Select</option>
                {vehicleMakes.map((make) => (
                  <option key={make} value={make}>
                    {make}
                  </option>
                ))}
              </select>

              <label htmlFor={`model-${index}`}>
                Vehicle Model
                {errors[index]?.model && (
                  <span className="error-message">{errors[index].model}</span>
                )}
              </label>
              <select
                id={`model-${index}`}
                name="model"
                value={vehicle.model}
                onChange={(e) => handleChange(index, e)}
                disabled={!vehicle.make}
                className={errors[index]?.model ? "error-field" : ""}
              >
                <option value="">Please Select</option>
                {vehicleModel[vehicle.make]?.map((model) => (
                  <option key={model} value={model}>
                    {model}
                  </option>
                ))}
              </select>

              <label htmlFor={`type-${index}`}>Vehicle Type</label>
              <select
                id={`type-${index}`}
                name="type"
                value={vehicle.type}
                onChange={(e) => handleChange(index, e)}
                disabled={!vehicle.model}
              >
                <option value="">Optional</option>
                {vehicleType[vehicle.make]?.[vehicle.model]?.map((type) => (
                  <option key={type} value={type}>
                    {type}
                  </option>
                ))}
              </select>

              <label htmlFor={`ownership-${index}`}>
                Is this vehicle owned, financed or leased?
              </label>
              <select
                id={`ownership-${index}`}
                name="ownership"
                value={vehicle.ownership}
                onChange={(e) => handleChange(index, e)}
              >
                <option value="Owned (not making loan payments)">
                  Owned (not making loan payments)
                </option>
                <option value="Financed (making loan payments)">
                  Financed (making loan payments)
                </option>
                <option value="Leased">Leased</option>
              </select>

              <label htmlFor={`mileage-${index}`}>
                What is the average annual mileage for this vehicle?
                {errors[index]?.mileage && (
                  <span className="error-message">{errors[index].mileage}</span>
                )}
              </label>
              <select
                id={`mileage-${index}`}
                name="mileage"
                value={vehicle.mileage}
                onChange={(e) => handleChange(index, e)}
                className={errors[index]?.mileage ? "error-field" : ""}
              >
                <option value="">Please Select</option>
                {[
                  "5,000 miles",
                  "10,000 miles",
                  "15,000 miles",
                  "20,000 miles",
                  "25,000 miles",
                  "30,000 miles",
                  "40,000 miles",
                  "50,000 miles",
                ].map((mileage) => (
                  <option key={mileage} value={mileage}>
                    {mileage}
                  </option>
                ))}
              </select>

              <label htmlFor={`primaryUse-${index}`}>
                What is the primary use for this vehicle?
              </label>
              <select
                id={`primaryUse-${index}`}
                name="primaryUse"
                value={vehicle.primaryUse}
                onChange={(e) => handleChange(index, e)}
              >
                <option value="Commute to Work">Commute to Work</option>
                <option value="Personal Use">Personal Use</option>
                <option value="Business">Business</option>
              </select>

              <div className="add-vehicle-button">
                {index > 0 && (
                  <button
                    className="remove-vehicle-button"
                    onClick={() => handleRemoveVehicle(index)}
                  >
                    Remove Vehicle
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}

        <button className="add-vehicle-button" onClick={handleAddVehicle}>
          + Add Another Vehicle (Optional)
        </button>

        <button className="next-step-button" onClick={handleSubmit}>
          Next Step
        </button>
      </div>
      <Footer />
    </div>
  );
};

export default StepOne2;
