import React, { useState } from "react";
import "../../styles/flowTwo/StepTwo2.css";
import Header from "../UserFlowOne/Header";
import Footer from "../UserFlowTwo/Footer";

const DriverInformation = ({ nextStep }) => {
  const [drivers, setDrivers] = useState([
    {
      name: "",
      dob: "",
      gender: "",
      maritalStatus: "Single",
      insured: "Yes",
      insuredForYear: "Yes",
      sr22: "No",
      dui: "No",
      accidents: "0",
      homeOwner: "Yes",
      military: "No",
      creditScore: "Good",
    },
  ]);
  const [errors, setErrors] = useState([]);

  const handleInputChange = (index, e) => {
    const { name, value } = e.target;
    const updatedDrivers = [...drivers];
    updatedDrivers[index][name] = value;
    setDrivers(updatedDrivers);

    // Clear errors for the updated field
    const updatedErrors = [...errors];
    updatedErrors[index] = {
      ...updatedErrors[index],
      [name]: false,
    };
    setErrors(updatedErrors);
  };

  const handleAddDriver = () => {
    setDrivers([
      ...drivers,
      {
        name: "",
        dob: "",
        gender: "",
        maritalStatus: "Single",
        insured: "Yes",
        insuredForYear: "Yes",
        sr22: "No",
        dui: "No",
        accidents: "0",
        homeOwner: "Yes",
        military: "No",
        creditScore: "Good",
      },
    ]);
    setErrors([...errors, {}]);
  };

  const validateFields = (index) => {
    const driver = drivers[index];
    const newErrors = {};
    if (!driver.name)
      newErrors.name = "Please enter the driver's first and last name";
    if (!driver.dob) newErrors.dob = "This field is required";
    if (!driver.gender) newErrors.gender = "Please select a gender";

    const updatedErrors = [...errors];
    updatedErrors[index] = newErrors;
    setErrors(updatedErrors);

    return Object.keys(newErrors).length === 0; // Valid if no errors
  };

  const handleSubmit = () => {
    const isValid = drivers.every((_, index) => validateFields(index));
    if (isValid) {
      nextStep(drivers); // Pass driver data to the next step
    }
  };

  return (
    <div>
      <Header />
      <h2>Driver's Information</h2>
      {drivers.map((driver, index) => (
        <div key={index} className="driver-form-section">
          <h3>Driver {index + 1}</h3>
          <div className="form-group">
            <label>
              First and Last Name
              {errors[index]?.name && (
                <span className="error-message">{errors[index].name}</span>
              )}
            </label>
            <input
              type="text"
              name="name"
              placeholder="Enter driver's first and last name"
              value={driver.name}
              onChange={(e) => handleInputChange(index, e)}
            />
          </div>

          <div className="form-group">
            <label>
              Date of Birth
              {errors[index]?.dob && (
                <span className="error-message">{errors[index].dob}</span>
              )}
            </label>
            <input
              type="date"
              name="dob"
              value={driver.dob}
              onChange={(e) => handleInputChange(index, e)}
            />
          </div>

          <div className="form-group">
            <label>Gender</label>
            <div className="radio-group">
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="Male"
                  checked={driver.gender === "Male"}
                  onChange={(e) => handleInputChange(index, e)}
                />
                Male
              </label>
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="Female"
                  checked={driver.gender === "Female"}
                  onChange={(e) => handleInputChange(index, e)}
                />
                Female
              </label>
            </div>
          </div>

          <div className="form-group">
            <label>Marital Status</label>
            <select
              name="maritalStatus"
              value={driver.maritalStatus}
              onChange={(e) => handleInputChange(index, e)}
            >
              <option value="Single">Single</option>
              <option value="Married">Married</option>
              <option value="Divorced">Divorced</option>
              <option value="Widowed">Widowed</option>
            </select>
          </div>

          <div className="form-group">
            <label>Are you currently insured?</label>
            <div className="radio-group">
              <label>
                <input
                  type="radio"
                  name="insured"
                  value="Yes"
                  checked={driver.insured === "Yes"}
                  onChange={(e) => handleInputChange(index, e)}
                />
                Yes
              </label>
              <label>
                <input
                  type="radio"
                  name="insured"
                  value="No"
                  checked={driver.insured === "No"}
                  onChange={(e) => handleInputChange(index, e)}
                />
                No
              </label>
            </div>
          </div>

          {/* Add other fields similarly */}
        </div>
      ))}

      <button className="add-driver-button" onClick={handleAddDriver}>
        + Add Another Driver (Optional)
      </button>

      <button className="next-step-button" onClick={handleSubmit}>
        Next Step
      </button>
    </div>
  );
};

export default DriverInformation;
