import React, { useState } from "react";
import { vehicleMakes } from "../../constants/vehicleMakes";
import { vehicleModel } from "../../constants/vehicleModel"; // Import models
import { vehicleTypes } from "../../constants/vehicleTypes"; // Import types

const StepOne2 = ({ nextStep, addAnotherVehicle }) => {
  const [vehicleDetails, setVehicleDetails] = useState({
    year: "",
    make: "",
    model: "",
    type: "",
    ownership: "Owned (not making loan payments)",
    mileage: "10,000 miles",
    primaryUse: "Commute to Work",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setVehicleDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));

    // Reset dependent fields
    if (name === "make") {
      setVehicleDetails((prevDetails) => ({
        ...prevDetails,
        model: "",
        type: "",
      }));
    } else if (name === "model") {
      setVehicleDetails((prevDetails) => ({
        ...prevDetails,
        type: "",
      }));
    }
  };

  const handleSubmit = () => {
    nextStep(vehicleDetails);
  };

  // Get available models for the selected make
  const availableModels =
    vehicleDetails.make && vehicleModel[vehicleDetails.make]
      ? vehicleModel[vehicleDetails.make]
      : [];

  // Get available types for the selected model
  const availableTypes =
    vehicleDetails.make &&
    vehicleDetails.model &&
    vehicleTypes[vehicleDetails.make] &&
    vehicleTypes[vehicleDetails.make][vehicleDetails.model]
      ? vehicleTypes[vehicleDetails.make][vehicleDetails.model]
      : [];

  return (
    <div>
      <h2>About Your Vehicle</h2>

      <label htmlFor="make">Vehicle Make</label>
      <select
        id="make"
        name="make"
        value={vehicleDetails.make}
        onChange={handleChange}
      >
        <option value="">Please Select</option>
        {vehicleMakes.map((make) => (
          <option key={make} value={make}>
            {make}
          </option>
        ))}
      </select>

      <label htmlFor="model">Vehicle Model</label>
      <select
        id="model"
        name="model"
        value={vehicleDetails.model}
        onChange={handleChange}
        disabled={!vehicleDetails.make}
      >
        <option value="">Please Select</option>
        {availableModels.map((model) => (
          <option key={model} value={model}>
            {model}
          </option>
        ))}
      </select>

      <label htmlFor="type">Vehicle Type</label>
      <select
        id="type"
        name="type"
        value={vehicleDetails.type}
        onChange={handleChange}
        disabled={!vehicleDetails.model}
      >
        <option value="">Please Select</option>
        {availableTypes.map((type) => (
          <option key={type} value={type}>
            {type}
          </option>
        ))}
      </select>

      <button onClick={addAnotherVehicle}>
        + Add Another Vehicle (Optional)
      </button>
      <button onClick={handleSubmit}>Next Step</button>
    </div>
  );
};

export default StepOne2;
