import React, { useState, useEffect } from "react";
import "../../styles/flowTwo/StepThree2.css";
import Header from "../UserFlowOne/Header";
import Footer from "./Footer";

const StepThree2 = () => {
  const [selectedCoverage, setSelectedCoverage] = useState("standard");
  const [zipCode] = useState(localStorage.getItem("zip_code") || ""); // Get ZIP code from localStorage
  const [location, setLocation] = useState("your area");

  const [formData, setFormData] = useState({
    address: "",
    cityStateZip: "",
    email: "",
    phone: "",
  });

  const [errors, setErrors] = useState({});

  // Fetch location based on ZIP code
  useEffect(() => {
    const fetchLocation = async () => {
      if (!zipCode) return;

      try {
        const response = await fetch(`https://api.zippopotam.us/us/${zipCode}`);
        if (!response.ok) {
          throw new Error("Invalid ZIP code or API error");
        }

        const data = await response.json();
        const state = data?.places?.[0]?.["state abbreviation"];
        const city = data?.places?.[0]?.["place name"];
        const zip = data?.["post code"];
        setLocation(city + ", " + state + ", " + zip || "your area");
      } catch (error) {
        console.error("Error fetching location:", error.message);
        setLocation("your area");
      }
    };

    fetchLocation();
  }, [zipCode]);

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validate form inputs
  const validateForm = () => {
    const errors = {};

    // Validate address
    if (!formData.address.trim()) {
      errors.address = "Street Address is required.";
    }

    // Validate city, state, and ZIP code
    if (!formData.cityStateZip.trim()) {
      errors.cityStateZip = "City, State, and ZIP Code are required.";
    } else if (!/^\w+,\s*\w+,\s*\d{5}$/.test(formData.cityStateZip)) {
      errors.cityStateZip = "Please enter a valid City, State, and ZIP Code.";
    }

    // Validate email
    if (!formData.email.trim()) {
      errors.email = "Email Address is required.";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      errors.email = "Please enter a valid email address.";
    }

    // Validate phone number
    if (!formData.phone.trim()) {
      errors.phone = "Phone Number is required.";
    } else if (!/^\(\d{3}\)\s\d{3}-\d{4}$/.test(formData.phone)) {
      errors.phone = "Phone Number must be in the format (xxx) xxx-xxxx.";
    }

    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      alert("Form submitted successfully!");
      // Submission logic goes here
    }
  };

  return (
    <div>
      <Header />
      <div className="quote-form-container">
        <h3 className="title">
          <span className="step-number">3.</span> Contact Details and See Quote
        </h3>
        <form className="form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Street Address</label>
            <input
              type="text"
              name="address"
              placeholder="Ex: 123 Main St."
              value={formData.address}
              onChange={handleInputChange}
              required
            />
            {errors.address && <p className="error">{errors.address}</p>}
          </div>
          <div className="form-group">
            <label>City, State and ZIP Code</label>
            <input
              type="text"
              name="cityStateZip"
              placeholder={location}
              value={formData.cityStateZip}
              onChange={handleInputChange}
              required
            />
            {errors.cityStateZip && (
              <p className="error">{errors.cityStateZip}</p>
            )}
          </div>
          <div className="form-group">
            <label>Email Address</label>
            <input
              type="email"
              name="email"
              placeholder="Ex: john@email.com"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
            {errors.email && <p className="error">{errors.email}</p>}
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="tel"
              name="phone"
              placeholder="(xxx) xxx-xxxx"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
            {errors.phone && <p className="error">{errors.phone}</p>}
          </div>

          <h4 className="coverage-title">
            What type of rates do you want to see?
          </h4>
          <p className="coverage-description">Select a coverage option.</p>

          <table className="coverage-table">
            <thead>
              <tr>
                <th className="coverage-header"></th>
                <th
                  className={`coverage-header ${
                    selectedCoverage === "minimum" ? "selected" : ""
                  }`}
                  onClick={() => setSelectedCoverage("minimum")}
                >
                  MINIMUM
                </th>
                <th
                  className={`coverage-header ${
                    selectedCoverage === "standard" ? "selected" : ""
                  }`}
                  onClick={() => setSelectedCoverage("standard")}
                >
                  STANDARD
                </th>
                <th
                  className={`coverage-header ${
                    selectedCoverage === "premium" ? "selected" : ""
                  }`}
                  onClick={() => setSelectedCoverage("premium")}
                >
                  PREMIUM
                </th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="row-label">Bodily Injury</td>
                <td
                  className={selectedCoverage === "minimum" ? "highlight" : ""}
                >
                  $25,000 / $50,000
                </td>
                <td
                  className={selectedCoverage === "standard" ? "highlight" : ""}
                >
                  $100,000 / $300,000
                </td>
                <td
                  className={selectedCoverage === "premium" ? "highlight" : ""}
                >
                  $250,000 / $500,000
                </td>
              </tr>
              <tr>
                <td className="row-label">Property Damage</td>
                <td
                  className={selectedCoverage === "minimum" ? "highlight" : ""}
                >
                  $10,000
                </td>
                <td
                  className={selectedCoverage === "standard" ? "highlight" : ""}
                >
                  $50,000
                </td>
                <td
                  className={selectedCoverage === "premium" ? "highlight" : ""}
                >
                  $100,000
                </td>
              </tr>
              <tr>
                <td className="row-label">Medical</td>
                <td
                  className={selectedCoverage === "minimum" ? "highlight" : ""}
                >
                  You pay
                </td>
                <td
                  className={selectedCoverage === "standard" ? "highlight" : ""}
                >
                  Insurance pays
                </td>
                <td
                  className={selectedCoverage === "premium" ? "highlight" : ""}
                >
                  Insurance pays
                </td>
              </tr>
              <tr>
                <td className="row-label">Roadside / Rental</td>
                <td
                  className={selectedCoverage === "minimum" ? "highlight" : ""}
                >
                  You pay
                </td>
                <td
                  className={selectedCoverage === "standard" ? "highlight" : ""}
                >
                  Insurance pays
                </td>
                <td
                  className={selectedCoverage === "premium" ? "highlight" : ""}
                >
                  Insurance pays
