// src/components/StepSix.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./../Form.css"; // Import your stylesheet here
import { CSSTransition } from "react-transition-group";

const StepSix = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  // Handler to update the form data when an option is selected
  const handleSelection = (bundleInterest) => {
    setFormData({ ...formData, bundleInterest });
  };

  const handlePrevious = () => {
    navigate("/step-five");
  };

  const handleNext = () => {
    navigate("/summary");
  };

  return (
    <CSSTransition
      in={true}
      appear={true}
      timeout={500}
      classNames="fade-slide"
    >
      <div className="form-container">
        {/* Progress bar container with dynamic width */}
        <div className="progress-bar-container">
          <div className="progress-bar" style={{ width: "75%" }}></div>
        </div>
        <h1>
          Would You be Interested in Bundling Your Auto and Home Insurance?
        </h1>
        <label htmlFor="numVehicles" className="savings-label">
          Bundle it Can Save Up to 30%
        </label>
        <div className="options-container">
          <button
            className={`option-button ${
              formData.bundleInterest === "Yes" ? "selected" : ""
            }`}
            onClick={() => handleSelection("Yes")}
          >
            Yes
          </button>
          <button
            className={`option-button ${
              formData.bundleInterest === "No" ? "selected" : ""
            }`}
            onClick={() => handleSelection("No")}
          >
            No
          </button>
        </div>
        <div className="buttons-container">
          <button onClick={handleNext}>Next</button>
          <button className="secondary-button" onClick={handlePrevious}>
            Previous
          </button>
        </div>
      </div>
    </CSSTransition>
  );
};

export default StepSix;
