import React, { useEffect, useState, useRef } from "react";
import Header from "./Header";
import Footer from "./Footer";
import "../../styles/flowOne/OffersPage.css";
import { CSSTransition } from "react-transition-group";

const OffersPage = () => {
  const [cityName, setCityName] = useState("your area");
  const [isGoogleTraffic, setIsGoogleTraffic] = useState(false);
  const nodeRef = useRef(null);

  useEffect(() => {
    const loadOffersData = async () => {
      try {
        const params = new URLSearchParams(window.location.search);

        const zipCode =
          params.get("zip") || localStorage.getItem("zip_code") || "00000";
        const insured =
          params.get("insured") || localStorage.getItem("insured");
        const numVehicles =
          params.get("vehicles") || localStorage.getItem("numVehicles");
        const numDrivers =
          params.get("drivers") || localStorage.getItem("numDrivers");
        const homeOwnership =
          params.get("home_ownership") ||
          localStorage.getItem("home_ownership") ||
          "00000";
        const campaignName =
          params.get("campaign_name") ||
          localStorage.getItem("campaign_name") ||
          "default_campaign";
        const campaignId =
          params.get("campaign_id") ||
          localStorage.getItem("campaign_id") ||
          "default_id";
        const clickId =
          params.get("click_id") ||
          localStorage.getItem("click_id") ||
          "default_click";

        // Retrieve the traffic source from localStorage
        const trafficSource = localStorage.getItem("traffic_source");

        // Check if the source is Google
        if (trafficSource && trafficSource.toLowerCase() === "google") {
          setIsGoogleTraffic(true);
        }

        // Load MediaAlpha script and wait for it to be ready
        await loadMediaAlphaScript();

        // Retry until MediaAlphaExchange__load becomes available
        await retryUntilAvailable(
          () => typeof window.MediaAlphaExchange__load === "function",
          500,
          5
        );

        // Initialize the MediaAlpha ad
        window.MediaAlphaExchange = {
          data: {
            zip: zipCode,
            currently_insured: insured,
            "vehicles[]": numVehicles,
            "drivers[]": numDrivers,
            home_ownership: homeOwnership,
          },
          placement_id: isGoogleTraffic
            ? "k7ImWb8FHOIBYxJQPmKSAUiyBQx_kg"
            : "fD4petSMP9HeM2IAYex88RKg0siJ_Q",
          sub_1: campaignName,
          sub_2: campaignId,
          sub_3: clickId,
          type: "ad_unit",
          version: 17,
        };

        window.MediaAlphaExchange__load("mediaalpha_placeholder");

        // Fetch city name from ZIP code
        if (zipCode !== "00000") {
          await fetchCityName(zipCode);
        }
      } catch (error) {
        console.error("An error occurred during useEffect execution.", error);
      }
    };

    loadOffersData();
  }, [isGoogleTraffic]);

  // Function to load the MediaAlpha script
  const loadMediaAlphaScript = async () => {
    return new Promise((resolve, reject) => {
      // Check if the script is already loaded
      if (document.getElementById("mediaalpha-script")) {
        resolve(); // If it's already loaded, just resolve immediately
        return;
      }

      const mediaAlphaScript = document.createElement("script");
      mediaAlphaScript.id = "mediaalpha-script";
      mediaAlphaScript.src = "//insurance.mediaalpha.com/js/serve.js";
      mediaAlphaScript.async = true;

      mediaAlphaScript.onload = () => {
        console.log("MediaAlpha script loaded successfully.");
        resolve(); // Script has been loaded successfully
      };
      mediaAlphaScript.onerror = (error) => {
        console.error("Failed to load MediaAlpha script.", error);
        reject(error); // There was an error loading the script
      };

      document.body.appendChild(mediaAlphaScript);
    });
  };

  // Function to retry until a condition is met or retries are exhausted
  const retryUntilAvailable = async (conditionFn, delay, retries) => {
    for (let i = 0; i < retries; i++) {
      if (conditionFn()) {
        return;
      }
      await new Promise((resolve) => setTimeout(resolve, delay));
    }
    throw new Error("Function not available after retries.");
  };

  const fetchCityName = async (zipCode) => {
    try {
      const response = await fetch(`https://api.zippopotam.us/us/${zipCode}`);
      if (response.ok) {
        const data = await response.json();
        const city = data.places[0]["place name"];
        setCityName(city);
      }
    } catch (error) {
      console.error("Failed to fetch city name:", error);
    }
  };

  // Function to handle ad click and track conversion
  const handleAdClick = () => {
    if (isGoogleTraffic) {
      // Google conversion tracking
      if (typeof window.gtag_report_conversion === "function") {
        window.gtag_report_conversion();
      } else {
        console.warn("Google conversion tracking function is not available.");
      }
    } else {
      // Facebook lead tracking pixel (assuming the fbq function is loaded)
      if (typeof window.fbq === "function") {
        window.fbq("track", "Lead");
      } else {
        console.warn("Facebook pixel tracking function is not available.");
      }
    }
  };

  return (
    <CSSTransition
      nodeRef={nodeRef}
      in={true}
      appear={true}
      timeout={1500}
      classNames="fade-slide"
    >
      <div ref={nodeRef}>
        <Header />
        <div className="offers-page-container">
          <div className="offers-header">
            <h2>We've Matched You with Great Offers in {cityName}!</h2>
            <p className="subtext">
              USA Insurance Reviews works with many reverse-bidding insurance
              companies. Based on your answers, we have matched you with the top
              aggregator in {cityName} that can get you the best possible deal.
            </p>
          </div>

          <div className="offers-list">
            {/* Placeholder for the MediaAlpha ad unit */}
            <div
              id="mediaalpha_placeholder"
              className="mediaalpha-container"
              onClick={handleAdClick} // Track ad clicks
            ></div>
          </div>

          <p className="powered-by-text">
            Powered by <span className="brand">usainsurancereviews.com</span>
          </p>
        </div>
        <Footer />
      </div>
    </CSSTransition>
  );
};

export default OffersPage;
