// src/components/StepOne.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./../Form.css"; // Import your stylesheet here

const StepTwo = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  const handleNext = () => {
    navigate("/step-three");
  };

  return (
    <div className="form-container">
      <h1>Step 1: Are You Currently Insured!!?</h1>
      <label htmlFor="insured">Currently Insured</label>
      <select
        id="insured"
        value={formData.insured}
        onChange={(e) => setFormData({ ...formData, insured: e.target.value })}
      >
        <option value="">Select</option>
        <option value="yes">Yes</option>
        <option value="no">No</option>
      </select>
      <div className="buttons-container">
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default StepTwo;
