// src/components/StepOne.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./../Form.css"; // Import your stylesheet here

const StepThree = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  // Handler to update the form data when an option is selected
  const handleSelection = (ageGroup) => {
    setFormData({ ...formData, ageGroup });
  };

  const handlePrevious = () => {
    navigate("/step-two");
  };

  const handleNext = () => {
    navigate("/step-four");
  };

  return (
    <div className="form-container">
      <h1>How Many Drivers Will Be Listed?</h1>
      <select
        id="numDrivers"
        value={formData.numDrivers}
        onChange={(e) =>
          setFormData({ ...formData, numDrivers: e.target.value })
        }
      >
        <option value="">Select</option>
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
        <option value="4">4</option>
      </select>
      <div className="buttons-container">
        <button onClick={handlePrevious}>Previous</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default StepThree;
