// src/components/StepTwo.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./../Form.css"; // Import the CSS styles
import { CSSTransition } from "react-transition-group";

const StepTwo = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  const handlePrevious = () => {
    navigate("/step-one");
  };

  const handleNext = (ageGroup) => {
    setFormData({ ...formData, ageGroup });
    navigate("/step-three");
  };

  return (
    <CSSTransition
      in={true}
      appear={true}
      timeout={500}
      classNames="fade-slide"
    >
      <div className="form-container">
        {/* Progress bar container with dynamic width */}
        <div className="progress-bar-container">
          <div className="progress-bar" style={{ width: "10%" }}></div>
        </div>
        <h1>Which Age Group Are You In?</h1>
        <div className="age-group-container">
          <button
            className={`option-button ${
              formData.ageGroup === "16-20" ? "selected" : ""
            }`}
            onClick={() => handleNext("16-20")}
          >
            16-20
          </button>
          <button
            className={`option-button ${
              formData.ageGroup === "21-24" ? "selected" : ""
            }`}
            onClick={() => handleNext("21-24")}
          >
            21-24
          </button>
          <button
            className={`option-button ${
              formData.ageGroup === "25-34" ? "selected" : ""
            }`}
            onClick={() => handleNext("25-34")}
          >
            25-34
          </button>
          <button
            className={`option-button ${
              formData.ageGroup === "35-44" ? "selected" : ""
            }`}
            onClick={() => handleNext("35-44")}
          >
            35-44
          </button>
          <button
            className={`option-button ${
              formData.ageGroup === "45-54" ? "selected" : ""
            }`}
            onClick={() => handleNext("45-54")}
          >
            45-54
          </button>
          <button
            className={`option-button ${
              formData.ageGroup === "55-64" ? "selected" : ""
            }`}
            onClick={() => handleNext("55-64")}
          >
            55-64
          </button>
          <button
            className={`option-button ${
              formData.ageGroup === "65+" ? "selected" : ""
            }`}
            onClick={() => handleNext("65+")}
          >
            65+
          </button>
        </div>
        <div className="buttons-container">
          <button onClick={handlePrevious}>Previous</button>
        </div>
      </div>
    </CSSTransition>
  );
};

export default StepTwo;
