import React from "react";
import "./ZipCodePage.css"; // Your CSS file for styling

const ZipCodePage = ({ nextStep, updateFormData }) => {
  const [zipCode, setZipCode] = React.useState("");
  const [error, setError] = React.useState("");

  const handleInputChange = (e) => {
    setZipCode(e.target.value);
    setError(""); // Clear error on input change
  };

  const handleSubmit = () => {
    if (/^\d{5}$/.test(zipCode)) {
      updateFormData("zipCode", zipCode); // Save data
      nextStep(); // Go to the next step
    } else {
      setError("Please enter a valid US ZIP Code");
    }
  };

  return (
    <div className="zip-code-container">
      {/* Logo */}
      <div className="logo-container">
        <img
          src="https://www.allstate.com/dist/content-client-react-app/static/media/logo-large.dd47a938943f3af25de5f6885d2c39d8.svg"
          alt="Allstate Logo"
          className="logo"
        />
      </div>

      {/* ZIP Code Form */}
      <h1>Find the lowest car insurance rates in your area!</h1>
      <div className="zip-code-box">
        <label htmlFor="zipCode">What is your ZIP Code?</label>
        <input
          type="text"
          id="zipCode"
          placeholder="ZIP Code"
          value={zipCode}
          onChange={handleInputChange}
        />
        <button onClick={handleSubmit}>Start Now</button>
        {error && <p className="error-message">{error}</p>}
      </div>
    </div>
  );
};

export default ZipCodePage;
