// src/components/StepSix.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./../Form.css"; // Import your stylesheet here

const StepSix = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  const handlePrevious = () => {
    navigate("/step-five");
  };

  const handleNext = () => {
    navigate("/summary");
  };

  return (
    <div className="form-container">
      {/* Progress bar container with dynamic width */}
      <div className="progress-bar-container">
        <div className="progress-bar" style={{ width: "75%" }}></div>
      </div>
      <h1>Would You be Interested in Bundling Your Auto and Home Insurance?</h1>
      <label htmlFor="numVehicles">Bundle it Can Save Up to 30%</label>

      <select
        id="homeOwnership"
        value={formData.homeOwnership}
        onChange={(e) =>
          setFormData({ ...formData, homeOwnership: e.target.value })
        }
      >
        <option value="">Select</option>
        <option value="yes">Yes</option>
        <option value="no">No</option>
      </select>
      <div className="buttons-container">
        <button onClick={handleNext}>Next</button>
        <button className="secondary-button" onClick={handlePrevious}>
          Previous
        </button>
      </div>
    </div>
  );
};

export default StepSix;
