// src/components/StepTwo.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./Form.css"; // Import the CSS styles

const StepTwo = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  // Handler to update the form data when an option is selected
  const handleSelection = (ageGroup) => {
    setFormData({ ...formData, ageGroup });
  };

  const handlePrevious = () => {
    navigate("/step-one");
  };

  const handleNext = () => {
    navigate("/step-three");
  };

  return (
    <div className="form-container">
      <h1>Which Age Group Are You In?</h1>
      <div className="options-container">
        <button
          className={`option-button ${
            formData.ageGroup === "16-20" ? "selected" : ""
          }`}
          onClick={() => handleSelection("16-20")}
        >
          16-20
        </button>
        <button
          className={`option-button ${
            formData.ageGroup === "21-24" ? "selected" : ""
          }`}
          onClick={() => handleSelection("21-24")}
        >
          21-24
        </button>
        <button
          className={`option-button ${
            formData.ageGroup === "25-34" ? "selected" : ""
          }`}
          onClick={() => handleSelection("25-34")}
        >
          25-34
        </button>
        <button
          className={`option-button ${
            formData.ageGroup === "35-44" ? "selected" : ""
          }`}
          onClick={() => handleSelection("35-44")}
        >
          35-44
        </button>
        <button
          className={`option-button ${
            formData.ageGroup === "45-54" ? "selected" : ""
          }`}
          onClick={() => handleSelection("45-54")}
        >
          45-54
        </button>
        <button
          className={`option-button ${
            formData.ageGroup === "55-64" ? "selected" : ""
          }`}
          onClick={() => handleSelection("55-64")}
        >
          55-64
        </button>
        <button
          className={`option-button ${
            formData.ageGroup === "65+" ? "selected" : ""
          }`}
          onClick={() => handleSelection("65+")}
        >
          65+
        </button>
      </div>
      <div className="buttons-container">
        <button className="secondary-button" onClick={handlePrevious}>
          Previous
        </button>
        <button onClick={handleNext} disabled={!formData.ageGroup}>
          Next
        </button>
      </div>
    </div>
  );
};

export default StepTwo;
