// src/components/OffersPage.js
import React, { useContext, useEffect } from "react";
import { FormDataContext } from "../../contexts/FormDataContext";
import Header from "./Header";
import Footer from "./Footer";
import "../../styles/flowOne/OffersPage.css";
import { CSSTransition } from "react-transition-group";

// Helper function to get URL parameters
const getUrlParameter = (name) => {
  name = name.replace(/[\[]/, "\\[").replace(/[\]]/, "\\]");
  const regex = new RegExp("[\\?&]" + name + "=([^&#]*)");
  const results = regex.exec(window.location.search);
  return results === null
    ? ""
    : decodeURIComponent(results[1].replace(/\+/g, " "));
};

const OffersPage = () => {
  const { formData } = useContext(FormDataContext);

  useEffect(() => {
    // Extract campaign parameters from URL
    const campaignName = getUrlParameter("campaign_name") || "default_campaign";
    const campaignId = getUrlParameter("campaign_id") || "default_id";
    const clickId = getUrlParameter("click_id") || "default_click";

    // Load the MediaAlpha ad script once the page loads
    const script = document.createElement("script");
    script.type = "text/javascript";
    script.innerHTML = `
      var MediaAlphaExchange = {
         "data": {
            "zip": "${formData.zipCode || "00000"}"
         },
         "placement_id": "fD4petSMP9HeM2IAYex88RKg0siJ_Q",
         "sub_1": "${campaignName}",
         "sub_2": "${campaignId}",
         "sub_3": "${clickId}",
         "type": "ad_unit",
         "version": 17
      };
      MediaAlphaExchange__load('mediaalpha_placeholder');
    `;
    document.body.appendChild(script);

    // Add event listener for ad clicks to trigger a Facebook Pixel Lead event
    const adContainer = document.getElementById("mediaalpha_placeholder");

    const handleAdClick = () => {
      // Trigger Facebook Lead event
      if (window.fbq) {
        fbq("track", "Lead");
      }
    };

    if (adContainer) {
      adContainer.addEventListener("click", handleAdClick);
    }

    // Cleanup the event listener when the component unmounts
    return () => {
      if (adContainer) {
        adContainer.removeEventListener("click", handleAdClick);
      }
    };
  }, [formData.zipCode]);

  return (
    <CSSTransition
      in={true}
      appear={true}
      timeout={1500}
      classNames="fade-slide"
    >
      <>
        <Header />
        <div className="offers-page-container">
          <div className="offers-header">
            <h2>
              We've Matched You with Great Offers in{" "}
              <span>{formData.city}</span>!
            </h2>
            <p className="subtext">
              USA Insurance Reviews works with many reverse-bidding insurance
              companies. Based on your answers, we have matched you with the top
              aggregator in your area that can get you the best possible deal.
            </p>
            Click View My Quote to see the best prices.
            <p></p>
          </div>

          <div className="offers-list">
            {/* Placeholder for the MediaAlpha ad unit */}
            <div id="mediaalpha_placeholder"></div>
          </div>

          <p className="powered-by-text">
            Powered by <span className="brand">usainsurancereviews.com</span>
          </p>
        </div>
        <Footer />
      </>
    </CSSTransition>
  );
};

export default OffersPage;
