// src/components/OffersPage.js
import React, { useContext, useEffect } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import "./../OffersPage.css";

const OffersPage = () => {
  const { formData } = useContext(FormDataContext);
  const location = formData.zipCode || "Your Area";

  useEffect(() => {
    // When component mounts, instantiate and load the ad using the MediaAlphaExchange code
    const script = document.createElement("script");
    script.type = "text/javascript";
    script.innerHTML = `
      var MediaAlphaExchange = {
         "data": {
            "zip": "${formData.zipCode || "00000"}"
         },
         "placement_id": "fD4petSMP9HeM2IAYex88RKg0siJ_Q",
         "sub_1": "test sub id",
         "type": "ad_unit",
         "version": 17
      };
      MediaAlphaExchange__load('mediaalpha_placeholder');
    `;
    document.body.appendChild(script);
  }, [formData.zipCode]);

  return (
    <div className="offers-page-container">
      <div className="offers-header">
        <h2>
          We've Matched You with Great Offers in{" "}
          <span className="location-highlight">{location}</span>!
        </h2>
        <p className="subtext">
          Best matches for your criteria - Age {formData.ageGroup || "N/A"}
        </p>
        <p>
          Click on 2 or more offers below to compare quotes and maximize your
          savings
        </p>
      </div>

      <div className="offers-list">
        {/* Placeholder for the offers */}
        <div className="offer-card">
          <img
            src="https://via.placeholder.com/100"
            alt="Root Car Insurance"
            className="offer-logo"
          />
          <div className="offer-details">
            <h3>Root Car Insurance</h3>
            <p>
              Insurance that's simple, modern, and lives on your smartphone.
            </p>
            <ul>
              <li>Customize your coverage to fit your needs.</li>
              <li>File a claim right in the Root app, hassle-free.</li>
              <li>
                Something changes in your life? Update your policy at any time.
              </li>
            </ul>
          </div>
          <button className="get-quote-button">Get Quote</button>
        </div>

        <div className="offer-card">
          <img
            src="https://via.placeholder.com/100"
            alt="National Insurance"
            className="offer-logo"
          />
        </div>

        {/* Placeholder for the MediaAlpha ad unit */}
        <div id="mediaalpha_placeholder"></div>
      </div>

      <p className="powered-by-text">
        Powered by <span className="brand">insure.com</span>
      </p>
    </div>
  );
};

export default OffersPage;
