import React, { useState } from "react";
import "../../styles/flowTwo/StepOne2.css";
import Header from "../UserFlowOne/Header";
import Footer from "../UserFlowTwo/Footer";
import { vehicleMakes } from "../../constants/vehicleMakes";
import { vehicleModel } from "../../constants/vehicleModel";
import { vehicleType } from "../../constants/vehicleType";

const StepOne2 = ({ nextStep }) => {
  const [vehicleDetails, setVehicleDetails] = useState({
    year: "",
    make: "",
    model: "",
    type: "",
    ownership: "Owned (not making loan payments)",
    mileage: "10,000 miles",
    primaryUse: "Commute to Work",
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setVehicleDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));

    if (name === "make") {
      setVehicleDetails((prevDetails) => ({
        ...prevDetails,
        model: "",
        type: "",
      }));
    } else if (name === "model") {
      setVehicleDetails((prevDetails) => ({
        ...prevDetails,
        type: "",
      }));
    }

    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: "",
    }));
  };

  const validateFields = () => {
    const newErrors = {};
    if (!vehicleDetails.year) newErrors.year = "Vehicle year is required.";
    if (!vehicleDetails.make) newErrors.make = "Vehicle make is required.";
    if (!vehicleDetails.model) newErrors.model = "Vehicle model is required.";
    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateFields()) {
      nextStep(vehicleDetails);
    }
  };

  return (
    <div>
      <Header />
      <div className="vehicle-details-container">
        <h2>Get the Best Rates in Your Area!</h2>
        <p>
          Get your quote in just 3 simple steps. Enter your information below.
        </p>

        <div className="section-header">
          <span className="section-number">1</span>
          <h3>About Your Vehicle</h3>
        </div>

        <div className="form-section">
          <div className="form-row">
            <label htmlFor="year">
              Vehicle Year
              {errors.year && (
                <span className="error-message">{errors.year}</span>
              )}
            </label>
            <select
              id="year"
              name="year"
              value={vehicleDetails.year}
              onChange={handleChange}
              className={errors.year ? "error-field" : ""}
            >
              <option value="">Please Select</option>
              {Array.from(
                new Array(71),
                (_, index) => new Date().getFullYear() - index
              ).map((year) => (
                <option key={year} value={year}>
                  {year}
                </option>
              ))}
            </select>
          </div>

          <div className="form-row">
            <label htmlFor="make">
              Vehicle Make
              {errors.make && (
                <span className="error-message">{errors.make}</span>
              )}
            </label>
            <select
              id="make"
              name="make"
              value={vehicleDetails.make}
              onChange={handleChange}
              className={errors.make ? "error-field" : ""}
            >
              <option value="">Please Select</option>
              {vehicleMakes.map((make) => (
                <option key={make} value={make}>
                  {make}
                </option>
              ))}
            </select>
          </div>

          <div className="form-row">
            <label htmlFor="model">
              Vehicle Model
              {errors.model && (
                <span className="error-message">{errors.model}</span>
              )}
            </label>
            <select
              id="model"
              name="model"
              value={vehicleDetails.model}
              onChange={handleChange}
              disabled={!vehicleDetails.make}
              className={errors.model ? "error-field" : ""}
            >
              <option value="">Please Select</option>
              {vehicleModel[vehicleDetails.make]?.map((model) => (
                <option key={model} value={model}>
                  {model}
                </option>
              ))}
            </select>
          </div>

          <div className="form-row">
            <label htmlFor="type">Vehicle Type</label>
            <select
              id="type"
              name="type"
              value={vehicleDetails.type}
              onChange={handleChange}
              disabled={!vehicleDetails.model}
            >
              <option value="">Optional</option>
              {vehicleType[vehicleDetails.make]?.[vehicleDetails.model]?.map(
                (type) => (
                  <option key={type} value={type}>
                    {type}
                  </option>
                )
              )}
            </select>
          </div>

          <div className="form-row">
            <label htmlFor="ownership">
              Is this vehicle owned, financed or leased?
            </label>
            <select
              id="ownership"
              name="ownership"
              value={vehicleDetails.ownership}
              onChange={handleChange}
            >
              <option value="Owned (not making loan payments)">
                Owned (not making loan payments)
              </option>
              <option value="Financed (making loan payments)">
                Financed (making loan payments)
              </option>
              <option value="Leased">Leased</option>
            </select>
          </div>

          <div className="form-row">
            <label htmlFor="mileage">
              What is the average annual mileage for this vehicle?
            </label>
            <select
              id="mileage"
              name="mileage"
              value={vehicleDetails.mileage}
              onChange={handleChange}
            >
              <option value="">Please Select</option>
              {["5,000 miles", "10,000 miles", "15,000 miles"].map(
                (mileage) => (
                  <option key={mileage} value={mileage}>
                    {mileage}
                  </option>
                )
              )}
            </select>
          </div>

          <div className="form-row">
            <label htmlFor="primaryUse">
              What is the primary use for this vehicle?
            </label>
            <select
              id="primaryUse"
              name="primaryUse"
              value={vehicleDetails.primaryUse}
              onChange={handleChange}
            >
              <option value="Commute to Work">Commute to Work</option>
              <option value="Personal Use">Personal Use</option>
              <option value="Business">Business</option>
            </select>
          </div>

          <button className="add-vehicle-button">
            + Add Another Vehicle (Optional)
          </button>
        </div>

        <button className="next-step-button" onClick={handleSubmit}>
          Next Step
        </button>
      </div>
      <Footer />
    </div>
  );
};

export default StepOne2;
