import React, { useEffect, useState } from "react";
import Header from "../UserFlowOne/Header";
import Footer from "../UserFlowOne/Footer2";
import "../../styles/flowOne/OffersPage.css";
import { CSSTransition } from "react-transition-group";

const OffersPage = () => {
  const [cityName, setCityName] = useState("your area");

  useEffect(() => {
    const params = new URLSearchParams(location.search);

    const zipCode =
      params.get("zip") || localStorage.getItem("zip_code") || "00000";
    const insured = params.get("insured") || localStorage.getItem("insured");
    const numVehicles =
      params.get("vehicles") || localStorage.getItem("vehicles");
    const numDrivers = params.get("drivers") || localStorage.getItem("drivers");
    const homeOwnership =
      params.get("home") || localStorage.getItem("home_ownership");
    const campaignName =
      params.get("campaign_name") ||
      localStorage.getItem("campaign_name") ||
      "default_campaign";
    const campaignId =
      params.get("campaign_id") ||
      localStorage.getItem("campaign_id") ||
      "default_id";
    const clickId =
      params.get("click_id") ||
      localStorage.getItem("click_id") ||
      "default_click";

    // Load the MediaAlpha ad script with campaign data
    const script = document.createElement("script");
    script.type = "text/javascript";
    script.innerHTML = `
      var MediaAlphaExchange = {
         "data": {
            "zip": "${zipCode}",
            "currently_insured": "${insured}",
            "vehicles[]": "${numVehicles}",
            "drivers[]": "${numDrivers}",
            "home_ownership": "${homeOwnership}"
         },
         "placement_id": "fD4petSMP9HeM2IAYex88RKg0siJ_Q",
         "sub_1": "${campaignName}",
         "sub_2": "${campaignId}",
         "sub_3": "${clickId}",
         "type": "ad_unit",
         "version": 17
      };
      MediaAlphaExchange__load('mediaalpha_placeholder');
    `;
    document.body.appendChild(script);

    // Fetch city name from ZIP code
    if (zipCode !== "00000") {
      fetchCityName(zipCode);
    }

    const trackAdClick = () => {
      // Fire MediaGo conversion pixel when ad is clicked
      window._megoaa = window._megoaa || [];
      window._megoaa.push({
        type: "event",
        name: "MediaAlpha Click",
        acid: "804",
        pxd: "1167741519631674",
      });
    };

    // Attach click event listener to the MediaAlpha ad
    const mediaalphaContainer = document.getElementById(
      "mediaalpha_placeholder"
    );
    if (mediaalphaContainer) {
      mediaalphaContainer.addEventListener("click", trackAdClick);
    }

    return () => {
      // Cleanup event listener
      if (mediaalphaContainer) {
        mediaalphaContainer.removeEventListener("click", trackAdClick);
      }
    };
  }, []);

  const fetchCityName = async (zipCode) => {
    try {
      const response = await fetch(`https://api.zippopotam.us/us/${zipCode}`);
      if (response.ok) {
        const data = await response.json();
        const city = data.places[0]["place name"];
        setCityName(city);
      }
    } catch (error) {
      console.error("Failed to fetch city name:", error);
    }
  };

  return (
    <CSSTransition
      in={true}
      appear={true}
      timeout={1500}
      classNames="fade-slide"
    >
      <>
        <Header />
        <div className="offers-page-container">
          <div className="offers-header">
            <h2>We've Matched You with Great Offers in {cityName}!</h2>
            <p className="subtext">
              USA Insurance Reviews works with many reverse-bidding insurance
              companies. Based on your answers, we have matched you with the top
              aggregator in {cityName} that can get you the best possible deal.
            </p>
            Click View My Quote to see the best prices.
            <p></p>
          </div>

          <div className="offers-list">
            {/* Placeholder for the MediaAlpha ad unit */}
            <div
              id="mediaalpha_placeholder"
              className="mediaalpha-container"
            ></div>
          </div>

          <p className="powered-by-text">
            Powered by <span className="brand">usainsurancereviews.com</span>
          </p>
        </div>
        <Footer />
      </>
    </CSSTransition>
  );
};

export default OffersPage;
