import React, { useContext } from "react";
import { FormDataContext } from "../../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "../../styles/flowOne/Form.css"; // Import your stylesheet here
import { CSSTransition } from "react-transition-group";
import crash from "./../../images/crash.jpeg";
import loading from "./../../images/loading.png"; // Placeholder image for lazy loading
import LazyImage from "../UserFlowOne/LazyImage"; // Lazy loading component

const StepOne = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  const handlePrevious = () => {
    navigate("/");
  };

  const handleNext = (insured, event) => {
    // Get the clicked button element
    const button = event.currentTarget;

    // Add the 'green-active' class to make the button stay green
    button.classList.add("green-active");

    // Convert "Yes" to `1` and "No" to `0`
    const insuredValue = insured === "Yes" ? 1 : 0;

    // Save to localStorage
    localStorage.setItem("insured", insuredValue);

    // Update the form data in the context
    setFormData({ ...formData, insured });

    // Delay the navigation to show the button effect
    setTimeout(() => {
      navigate("/step-two");
    }, 400); // Delay for noticeable green effect
  };

  return (
    <CSSTransition
      in={true}
      appear={true}
      timeout={1500}
      classNames="fade-slide"
    >
      <div className="form-container">
        <div className="content-container">
          <LazyImage
            src={crash}
            alt="Get Insured Today"
            className="banner-image"
            placeholder={loading}
          />
        </div>
        {/* Progress bar container with dynamic width */}
        <div className="progress-bar-container">
          <div className="progress-bar" style={{ width: "5%" }}></div>
        </div>
        <h1>Are You Currently Insured?</h1>
        <div className="options-container">
          <button
            className={`option-button ${
              formData.insured === "Yes" ? "selected" : ""
            }`}
            onClick={(event) => handleNext("Yes", event)}
          >
            Yes
          </button>
          <button
            className={`option-button ${
              formData.insured === "No" ? "selected" : ""
            }`}
            onClick={(event) => handleNext("No", event)}
          >
            No
          </button>
        </div>
        <div className="buttons-container">
          <button onClick={handlePrevious}>Previous</button>
        </div>
      </div>
    </CSSTransition>
  );
};

export default StepOne;
