// src/components/StepFour.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./../Form.css"; // Import your stylesheet here

const StepFour = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  const handleNext = () => {
    navigate("/StepFive");
  };

  return (
    <div className="form-container">
      <h1>Would You Like to Insure More Than One Vehicle?</h1>
      <label htmlFor="numVehicles">You Can Save Up to 20%</label>
      <select
        id="numVehicles"
        value={formData.numVehicles}
        onChange={(e) =>
          setFormData({ ...formData, numVehicles: e.target.value })
        }
      >
        <option value="">Select</option>
        <option value="yes">Yes</option>
        <option value="no">No</option>
      </select>
      <div className="buttons-container">
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default StepFour;
