// src/components/StepFive.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./../Form.css"; // Import your stylesheet here
import { CSSTransition } from "react-transition-group";

const StepFive = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  // Handler to update the form data when an option is selected
  const handleSelection = (homeOwnership) => {
    setFormData({ ...formData, homeOwnership });
  };

  const handlePrevious = () => {
    navigate("/step-four");
  };

  const handleNext = () => {
    navigate(formData.homeOwnership === "Yes" ? "/step-six" : "/summary");
  };

  return (
    <CSSTransition
      in={true}
      appear={true}
      timeout={500}
      classNames="fade-slide"
    >
      <div className="form-container">
        {/* Progress bar container with dynamic width */}
        <div className="progress-bar-container">
          <div className="progress-bar" style={{ width: "55%" }}></div>
        </div>
        <h1>Do You Own Your Home?</h1>
        <div className="options-container">
          <button
            className={`option-button ${
              formData.homeOwnership === "Yes" ? "selected" : ""
            }`}
            onClick={() => handleSelection("Yes")}
          >
            Yes
          </button>
          <button
            className={`option-button ${
              formData.homeOwnership === "No" ? "selected" : ""
            }`}
            onClick={() => handleSelection("No")}
          >
            No
          </button>
        </div>
        <div className="buttons-container">
          <button onClick={handleNext} disabled={!formData.homeOwnership}>
            Next
          </button>
          <button className="secondary-button" onClick={handlePrevious}>
            Previous
          </button>
        </div>
      </div>
    </CSSTransition>
  );
};

export default StepFive;
