import React, { useEffect, useState } from "react";
import "./ZipCodePage.css";

const ZipCodePage2 = ({ nextStep, updateFormData }) => {
  const [zipCode, setZipCode] = useState("");
  const [error, setError] = useState("");
  const [location, setLocation] = useState("your area"); // Default location

  useEffect(() => {
    // Fetch user's location when the component loads
    const fetchLocation = async () => {
      try {
        const response = await fetch(
          "https://ipinfo.io?token=YOUR_ACCESS_TOKEN"
        );
        const data = await response.json();
        setLocation(data.region || "your area"); // Update location dynamically
      } catch (error) {
        console.error("Error fetching location:", error);
      }
    };

    fetchLocation();
  }, []);

  const handleInputChange = (e) => {
    setZipCode(e.target.value);
    setError(""); // Clear error on input change
  };

  const handleSubmit = () => {
    if (/^\d{5}$/.test(zipCode)) {
      updateFormData("zipCode", zipCode); // Save data
      nextStep(); // Go to the next step
    } else {
      setError("Please enter a valid US ZIP Code");
    }
  };

  return (
    <div className="zip-code-container">
      <h1>Find the lowest car insurance rates in {location}!</h1>
      <div className="zip-code-box">
        <label htmlFor="zipCode">What is your ZIP Code?</label>
        <input
          type="text"
          id="zipCode"
          placeholder="ZIP Code"
          value={zipCode}
          onChange={handleInputChange}
        />
        <button onClick={handleSubmit}>Start Now</button>
        {error && <p className="error-message">{error}</p>}
      </div>
      <div className="partners">
        <h4>OUR PARTNERS</h4>
        <div className="partner-logos">
          <img src="path-to-allstate-logo" alt="Allstate" />
          <img src="path-to-general-logo" alt="The General" />
          <img src="path-to-progressive-logo" alt="Progressive" />
          <span>+25 more</span>
        </div>
      </div>
    </div>
  );
};

export default ZipCodePage2;
