import React, { useState } from "react";
import "../../styles/flowTwo/StepOne2.css";
import Header from "../UserFlowOne/Header";
import Footer from "../UserFlowTwo/Footer";
import { vehicleMakes } from "../../constants/vehicleMakes";
import { vehicleModel } from "../../constants/vehicleModel";
import { vehicleType } from "../../constants/vehicleType";

const StepOne2 = ({ nextStep }) => {
  const [vehicles, setVehicles] = useState([
    {
      year: "",
      make: "",
      model: "",
      type: "",
      ownership: "Owned (not making loan payments)",
      mileage: "10,000 miles",
      primaryUse: "Commute to Work",
    },
  ]);

  const [errors, setErrors] = useState([]);

  const handleChange = (index, e) => {
    const { name, value } = e.target;
    const updatedVehicles = [...vehicles];
    updatedVehicles[index] = {
      ...updatedVehicles[index],
      [name]: value,
    };

    // Reset dependent fields when higher-level fields change
    if (name === "make") {
      updatedVehicles[index].model = "";
      updatedVehicles[index].type = "";
    } else if (name === "model") {
      updatedVehicles[index].type = "";
    }

    setVehicles(updatedVehicles);

    // Clear error for the changed field
    const updatedErrors = [...errors];
    updatedErrors[index] = { ...updatedErrors[index], [name]: "" };
    setErrors(updatedErrors);
  };

  const validateFields = () => {
    const newErrors = vehicles.map((vehicle) => {
      const vehicleErrors = {};
      if (!vehicle.year) vehicleErrors.year = "Vehicle year is required.";
      if (!vehicle.make) vehicleErrors.make = "Vehicle make is required.";
      if (!vehicle.model) vehicleErrors.model = "Vehicle model is required.";
      if (!vehicle.type) vehicleErrors.type = "Vehicle type is required.";
      if (!vehicle.mileage) vehicleErrors.mileage = "Mileage is required.";
      if (!vehicle.primaryUse)
        vehicleErrors.primaryUse = "Primary use is required.";
      return vehicleErrors;
    });

    setErrors(newErrors);

    // Return true if no errors exist in any vehicle
    return newErrors.every(
      (vehicleErrors) => Object.keys(vehicleErrors).length === 0
    );
  };

  const handleAddVehicle = () => {
    setVehicles([
      ...vehicles,
      {
        year: "",
        make: "",
        model: "",
        type: "",
        ownership: "Owned (not making loan payments)",
        mileage: "10,000 miles",
        primaryUse: "Commute to Work",
      },
    ]);
    setErrors([...errors, {}]);
  };

  const handleSubmit = () => {
    if (validateFields()) {
      nextStep(vehicles);
    }
  };

  return (
    <div>
      <Header />
      <div className="vehicle-details-container">
        <h2>Get the Best Rates in Your Area!</h2>
        <p>
          Get your quote in just 3 simple steps. Enter your information below.
        </p>

        <div className="section-header">
          <span className="section-number">1</span>
          <h3>About Your Vehicle</h3>
        </div>

        <div className="form-section">
          <label htmlFor="year">Vehicle Year</label>
          <select
            id="year"
            name="year"
            value={vehicleDetails.year}
            onChange={handleChange}
            className={vehicleDetails.year ? "" : "error-field"}
          >
            <option value="">Please Select</option>
            {Array.from(
              new Array(71),
              (_, index) => new Date().getFullYear() - index
            ).map((year) => (
              <option key={year} value={year}>
                {year}
              </option>
            ))}
          </select>

          <label htmlFor="make">Vehicle Make</label>
          <select
            id="make"
            name="make"
            value={vehicleDetails.make}
            onChange={handleChange}
            className={vehicleDetails.make ? "" : "error-field"}
          >
            <option value="">Please Select</option>
            {vehicleMakes.map((make) => (
              <option key={make} value={make}>
                {make}
              </option>
            ))}
          </select>

          <label htmlFor="model">Vehicle Model</label>
          <select
            id="model"
            name="model"
            value={vehicleDetails.model}
            onChange={handleChange}
            disabled={!vehicleDetails.make}
            className={vehicleDetails.model ? "" : "error-field"}
          >
            <option value="">Please Select</option>
            {vehicleModel[vehicleDetails.make]?.map((model) => (
              <option key={model} value={model}>
                {model}
              </option>
            ))}
          </select>

          <label htmlFor="type">Vehicle Type</label>
          <select
            id="type"
            name="type"
            value={vehicleDetails.type}
            onChange={handleChange}
            disabled={!vehicleDetails.model}
            className={vehicleDetails.type ? "" : "error-field"}
          >
            <option value="">Please Select</option>
            {vehicleType[vehicleDetails.make]?.[vehicleDetails.model]?.map(
              (type) => (
                <option key={type} value={type}>
                  {type}
                </option>
              )
            )}
          </select>

          <label htmlFor="ownership">
            Is this vehicle owned, financed or leased?
          </label>
          <select
            id="ownership"
            name="ownership"
            value={vehicleDetails.ownership}
            onChange={handleChange}
          >
            <option value="Owned (not making loan payments)">
              Owned (not making loan payments)
            </option>
            <option value="Financed (making loan payments)">
              Financed (making loan payments)
            </option>
            <option value="Leased">Leased</option>
          </select>

          <label htmlFor="mileage">
            What is the average annual mileage for this vehicle?
          </label>
          <select
            id="mileage"
            name="mileage"
            value={vehicleDetails.mileage}
            onChange={handleChange}
          >
            <option value="">Please Select</option>
            {[
              "5,000 miles",
              "7,500 miles",
              "10,000 miles",
              "12,500 miles",
              "20,000 miles",
              "25,000 miles",
              "30,000 miles",
              "40,000 miles",
              "50,000 miles",
            ].map((mileage) => (
              <option key={mileage} value={mileage}>
                {mileage}
              </option>
            ))}
          </select>

          <label htmlFor="primaryUse">
            What is the primary use for this vehicle?
          </label>
          <select
            id="primaryUse"
            name="primaryUse"
            value={vehicleDetails.primaryUse}
            onChange={handleChange}
          >
            <option value="Commute to Work">Commute to Work</option>
            <option value="Personal Use">Personal Use</option>
            <option value="Business">Business</option>
          </select>

          <button className="add-vehicle-button" onClick={addAnotherVehicle}>
            + Add Another Vehicle (Optional)
          </button>
        </div>

        <button
          className="next-step-button"
          onClick={handleSubmit}
          disabled={
            !vehicleDetails.year ||
            !vehicleDetails.make ||
            !vehicleDetails.model ||
            !vehicleDetails.type
          }
        >
          Next Step
        </button>
      </div>

      <Footer />
    </div>
  );
};

export default StepOne2;
