import React, { useState } from "react";
import "../../styles/flowTwo/StepTwo2.css";
import Header from "../UserFlowOne/Header";
import Footer from "./Footer";
import "react-datepicker/dist/react-datepicker.css";

const DriverInfo = () => {
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    dateOfBirth: "",
    insured: true,
    insuredForYear: true,
    sr22: false,
    dui: false,
    homeOwner: true,
    military: false,
    accidents: "0",
    creditScore: "Good",
    gender: "male",
    maritalStatus: "Single",
  });

  const [errors, setErrors] = useState({});

  const handleToggle = (key) => {
    setForm((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    const newErrors = {};
    if (!form.firstName || !/^[a-zA-Z]+$/.test(form.firstName)) {
      newErrors.firstName = "First name is required and must be valid.";
    }
    if (!form.lastName || !/^[a-zA-Z]+$/.test(form.lastName)) {
      newErrors.lastName = "Last name is required and must be valid.";
    }
    if (!form.dateOfBirth) {
      newErrors.dateOfBirth = "Date of birth is required.";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNextStep = () => {
    if (validateForm()) {
      alert("Form is valid. Proceeding to the next step...");
    }
  };

  return (
    <div>
      <Header />
      <div className="driver-info">
        <h2>Driver's Information</h2>

        <div className="driver-section">
          <div className="form-row">
            <div className="form-group">
              <label>First Name</label>
              <input
                type="text"
                name="firstName"
                placeholder="Enter first name"
                value={form.firstName}
                onChange={handleChange}
                className={errors.firstName ? "error" : ""}
              />
              {errors.firstName && (
                <span className="error-message">{errors.firstName}</span>
              )}
            </div>
            <div className="form-group">
              <label>Last Name</label>
              <input
                type="text"
                name="lastName"
                placeholder="Enter last name"
                value={form.lastName}
                onChange={handleChange}
                className={errors.lastName ? "error" : ""}
              />
              {errors.lastName && (
                <span className="error-message">{errors.lastName}</span>
              )}
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Date of Birth</label>
              <input
                type="date"
                name="dateOfBirth"
                value={form.dateOfBirth}
                onChange={handleChange}
                className={errors.dateOfBirth ? "error" : ""}
              />
              {errors.dateOfBirth && (
                <span className="error-message">{errors.dateOfBirth}</span>
              )}
            </div>
            <div className="form-group">
              <label>Gender</label>
              <div className="toggle-buttons">
                <button
                  className={form.gender === "male" ? "active" : ""}
                  onClick={() => setForm({ ...form, gender: "male" })}
                >
                  Male
                </button>
                <button
                  className={form.gender === "female" ? "active" : ""}
                  onClick={() => setForm({ ...form, gender: "female" })}
                >
                  Female
                </button>
              </div>
            </div>
          </div>

          <div className="form-grid">
            <FormToggle
              label="Are you currently insured?"
              active={form.insured}
              onToggle={() => handleToggle("insured")}
            />
            <FormToggle
              label="Have you been insured for at least a year?"
              active={form.insuredForYear}
              onToggle={() => handleToggle("insuredForYear")}
            />
            <FormDropdown
              label="Current Insurer"
              name="currentInsurer"
              options={["Other", "Geico", "State Farm"]}
              onChange={handleChange}
            />
            <FormToggle
              label="Has your state notified you to file an SR22?"
              active={form.sr22}
              onToggle={() => handleToggle("sr22")}
            />
            <FormToggle
              label="Do you have any DUIs in the past 3 years?"
              active={form.dui}
              onToggle={() => handleToggle("dui")}
            />
            <FormDropdown
              label="At fault accidents in the last 3 years?"
              name="accidents"
              options={["0", "1", "2"]}
              onChange={handleChange}
            />
            <FormToggle
              label="Do you own a Home?"
              active={form.homeOwner}
              onToggle={() => handleToggle("homeOwner")}
            />
            <FormToggle
              label="Military Affiliation?"
              active={form.military}
              onToggle={() => handleToggle("military")}
            />
            <FormDropdown
              label="Credit Score"
              name="creditScore"
              options={["Good", "Average", "Poor"]}
              onChange={handleChange}
            />
          </div>
        </div>

        <button className="next-step" onClick={handleNextStep}>
          Next Step
        </button>
      </div>
      <Footer />
    </div>
  );
};

const FormToggle = ({ label, active, onToggle }) => (
  <div className="form-group">
    <label>{label}</label>
    <div className="toggle-buttons">
      <button className={active ? "active" : ""} onClick={onToggle}>
        Yes
      </button>
      <button className={!active ? "active" : ""} onClick={onToggle}>
        No
      </button>
    </div>
  </div>
);

const FormDropdown = ({ label, name, options, onChange }) => (
  <div className="form-group">
    <label>{label}</label>
    <select name={name} onChange={onChange}>
      {options.map((option) => (
        <option value={option} key={option}>
          {option}
        </option>
      ))}
    </select>
  </div>
);

export default DriverInfo;
