// src/components/StepOne.js
import React, { useContext } from "react";
import { FormDataContext } from "../contexts/FormDataContext";
import { useNavigate } from "react-router-dom";
import "./../Form.css"; // Import your stylesheet here

const StepThree = () => {
  const { formData, setFormData } = useContext(FormDataContext);
  const navigate = useNavigate();

  // Handler to update the form data when an option is selected
  const handleSelection = (numDrivers) => {
    setFormData({ ...formData, numDrivers });
  };

  const handlePrevious = () => {
    navigate("/step-two");
  };

  const handleNext = () => {
    navigate("/step-four");
  };

  return (
    <div className="form-container">
      <h1>How Many Drivers Will Be Listed?</h1>
      <div className="options-container">
        <button
          className={`option-button ${
            formData.numDrivers === "1" ? "selected" : ""
          }`}
          onClick={() => handleSelection("1")}
        >
          1
        </button>
        <button
          className={`option-button ${
            formData.numDrivers === "2" ? "selected" : ""
          }`}
          onClick={() => handleSelection("2")}
        >
          2
        </button>
        <button
          className={`option-button ${
            formData.numDrivers === "3" ? "selected" : ""
          }`}
          onClick={() => handleSelection("3")}
        >
          3
        </button>
        <button
          className={`option-button ${
            formData.numDrivers === "4" ? "selected" : ""
          }`}
          onClick={() => handleSelection("4")}
        >
          4
        </button>
      </div>
      <div className="buttons-container">
        <button onClick={handleNext}>Next</button>
        <button className="secondary-button" onClick={handlePrevious}>
          Previous
        </button>
      </div>
    </div>
  );
};

export default StepThree;
